INSERT INTO `ai_product_temp` (`id`, `uid`, `title`, `amount`, `days`, `type`, `type_name`, `slogan`, `user_name`, `cellphone`, `idnumber`, `province`, `province_name`, `city`, `city_name`, `address`, `content`, `banner`, `source_video`, `video_url`, `addtime`, `updatetime`, `is_submit`) VALUES
(1, 1, '测试发布一个项目', '500000.00', 60, 3, 'IT', '我不想多说什么，你们这些货！', '黄石物语', '13456831303', '422802198808095018', 1057, '浙江省', 1058, '杭州', '拱墅区湖州街168号美好国际大厦12楼', '<p>\r\n	人生感情最美满的状态,莫过“愿得一人心,白首不相离”。然而造化弄人,相爱的人却未必能够相守。那些在言情剧中才有的狗血剧情,如果在现实中上演,往往更让人纠结、唏嘘,甚至暗自神伤。\r\n</p>\r\n<p>\r\n	近日,(浙江)宁波镇海法院审理的一起借贷纠纷,就是这样的一个感情故事。\r\n</p>\r\n<p>\r\n	法庭上,相爱的两人相对而坐,默默流泪。他们背后的旁听席上,两家人在无休止地争吵……\r\n</p>\r\n<p>\r\n	一起“奇怪”的借贷纠纷\r\n</p>\r\n<p>\r\n	债主说少还点;欠债的却不答应\r\n</p>\r\n<p>\r\n	债主说你少还点;借钱的说,不行,我要全部还你。如此通情达理的债务双方,这在法官的从业生涯中,恐怕遇到的也不多。\r\n</p>\r\n<p>\r\n	接到王小姐告张先生归还30万元的起诉时,一开始,镇海法院的法官还以为,这只不过是一起普通的民间借贷纠纷,并未特别在意。\r\n</p>\r\n<p>\r\n	前几天开庭审理,法官才发现了异样。\r\n</p>\r\n<p>\r\n	王小姐和张先生是一对刚分手的恋人。向前任讨债,法官见得多了。大多数的恋人都是这样,在一起时爱得如胶似漆、死去活来,分手后却形同陌路、相见成仇。法官见过最极端的例子,女方连送出去的价值一两元的小礼物都要讨回去。\r\n</p>\r\n<p>\r\n	但很快,法官就被这对“奇怪”恋人惊掉了下巴。\r\n</p>\r\n<p>\r\n	刚开审,讨债的女方却“大方”表示,不需要男方还30万元,还10万元就够了。而男方听了女方的说法,非但一点没有高兴的样子,反而坚持“我一定要全部还”。那么,又何苦要闹上法庭呢?法官有些好奇。\r\n</p>\r\n<p>\r\n	他为她创业奋斗\r\n</p>\r\n<p>\r\n	她为支持他甚至借了高利贷\r\n</p>', 'upload/products/201504/1429774715.jpg', 'http://v.youku.com/v_show/id_XOTM5OTY4NjE2.html?f=23720089&ev=2&from=y1.3-idx-uhome-1519-20887.205805-205902.2-1', 'http://player.youku.com/player.php/sid/XOTM5OTY4NjE2/v.swf', 1429693714, '2015-04-27 02:05:22', 1),
